import Slider from "react-slick";
import React from "react";
import styles from './index.less'
import Swiper from 'swiper/dist/js/swiper.js'
import 'swiper/dist/css/swiper.min.css'

export default class Banner extends React.Component {
  constructor(props) {
    super(props)
    this.state.banners = [
      {
        src: '/static/images/banner1.png',
        color: '#E94846'
      },
      {
        src: '/static/images/banner1.png',
        color: '#E94846'
      }
    ]
  }

  componentDidMount() {
    new Swiper('.swiper-container', {
      direction: 'vertical',//竖向轮播
      loop: true,//无缝轮播
      pagination: {//小圆点分页
        el: '.swiper-pagination',
      },
      navigation: {//左右分页
        nextEl: '.swiper-button-next',
        prevEl: '.swiper-button-prev',
      },
      scrollbar: {//下划线分页
        el: '.swiper-scrollbar',
      }
    })
  }

  state = {
    carouselBgColor: '#fbf0de'
  }

  render() {
    const settings = {
      dots: true,
      infinite: true,
      autoplay: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      // pauseOnFocus: true,
      dotsClass: 'slick-dots hhb-dot',
      // beforeChange: (oi, ni) => {
      //   this.setState({
      //     carouselBgColor: banners[ni].color
      //   })
      // }
    }

    const {banners} = this.state
    return (
      <div className="carousel">
          <div className="banner-wrap">
            {/*<Slider {...settings}>*/}
              {/*{*/}
                {/*banners.map(i => {*/}
                  {/*return (*/}
                    {/*<div className="banner-item" style={{background:`url(${i.src}) center center`}} key={i}>*/}
                      {/*121231*/}
                    {/*</div>*/}
                  {/*)*/}
                {/*})*/}
              {/*}*/}
            {/*</Slider>*/}
            <div className="swiper-container">

              <div className="swiper-wrapper">

                <div className="swiper-slide">Slide 1</div>
                <div className="swiper-slide">Slide 2</div>
                <div className="swiper-slide">Slide 3</div>
              </div>

              <div className="swiper-pagination"></div>

              <div className="swiper-button-prev"></div>
              <div className="swiper-button-next"></div>

              <div className="swiper-scrollbar"></div>
            </div>
        </div>
        <style lang="less" jsx>{styles}</style>
      </div>
    )


  }
}
