import css from 'styled-jsx/css';

export default css`.product {
  position: relative;
  height: 1004px;
}
.product .card {
  display: inline-block;
  line-height: 100%;
  width: 183px;
  height: 162px;
  margin-top: 85px;
  margin-left: 7px;
  margin-right: 7px;
  border: 2px solid #c2dbfd;
  box-shadow: 0px 12px 23px 4px rgba(216, 223, 229, 0.3);
}
.product .center-pic {
  position: absolute;
  width: 787px;
  height: 628px;
  margin: -54px auto 0;
  left: 0px;
  right: 0px;
}
.feature-left {
  height: 701px;
  background-color: #F8FAFF;
}
.feature-left .feature-content img {
  width: 257px;
  height: 521px;
  margin-top: 90px;
  margin-left: 180px;
}
.feature-left .feature-content .text {
  display: inline-block;
  vertical-align: top;
}
.feature-left .feature-content .text h2 {
  width: 145px;
  height: 34px;
  font-size: 36px;
  margin-top: 180px;
  margin-left: 226px;
  margin-bottom: 51px;
  font-family: Source Han Sans CN;
  font-weight: bold;
  color: #333333;
}
.feature-left .feature-content .text h3 {
  height: 23px;
  font-size: 24px;
  margin-left: 226px;
  margin-bottom: 30px;
  font-family: Source Han Sans CN;
  font-weight: 400;
  color: #666666;
  line-height: 36px;
}
.feature-left .feature-content .text p {
  height: 18px;
  font-size: 18px;
  margin-left: 226px;
  font-family: Source Han Sans CN;
  font-weight: 400;
  color: #666666;
  line-height: 36px;
}
.feature-right {
  height: 701px;
