import Slider from "react-slick";
import React from "react";
import styles from './index.less'

export default class Banner extends React.Component {
  constructor(props) {
    super(props)
    this.state.banners = [
      {
        src: '/static/images/banner1.png',
        color: '#E94846'
      }
    ]
  }


  state = {
    carouselBgColor: '#fbf0de'
  }

  render() {
    const settings = {
      dots: true,
      infinite: true,
      autoplay: true,
      slidesToShow: 1,
      fade: true,
      slidesToScroll: 1,
      // pauseOnFocus: true,
      dotsClass: 'slick-dots hhb-dot',
      // beforeChange: (oi, ni) => {
      //   this.setState({
      //     carouselBgColor: banners[ni].color
      //   })
      // }
    }

    const {banners} = this.state
    return (
      <div className="carousel" style={{backgroundColor: this.state.carouselBgColor}}>
        <div className="banner-layout">
          <div className="banner-wrap">
            <Slider {...settings}>
              {
                banners.map(i => {
                  return (
                    <div className="banner-item" key={i}>
                      <img src={i.src} key={i} alt=""/>
                    </div>
                  )
                })
              }
            </Slider>
          </div>
        </div>
        <style lang="less" jsx>{styles}</style>
      </div>
    )


  }
}
