import css from 'styled-jsx/css';

export default css`.news-card-wrap {
  width: 1197px;
  height: 323px;
  background: #ffffff;
  box-shadow: 0px 12px 23px 4px rgba(216, 223, 229, 0.65);
  box-sizing: border-box;
  padding: 41px 35px;
}
.news-card-wrap .pic-wrap {
  width: 400px;
  height: 241px;
  overflow: hidden;
  border: 1px solid #eee;
  float: left;
}
.news-card-wrap .pic-wrap img {
  width: 100%;
  height: 100%;
}
.news-card-wrap .content {
  padding-left: 80px;
  overflow: hidden;
}
.news-card-wrap .content .title {
  width: 600px;
  font-size: 30px;
  font-family: Source Han Sans CN;
  font-weight: bold;
  color: #333333;
  line-height: 24px;
}
.news-card-wrap .content .desc-wrap {
  width: 600px;
  height: 120px;
  margin-top: 30px;
  overflow-y: auto;
  border: 1px solid;
  margin-bottom: 30px;
}
.news-card-wrap .content .desc-wrap .desc {
  font-size: 20px;
  font-family: Source Han Sans CN;
  font-weight: 400;
  color: #666666;
  line-height: 30px;
}
.news-card-wrap .content .date-wrap {
  height: 30px;
}
`
